{
	"patch":
	{
		"@odata.type": "#microsoft.substrate.ai.management.updateSynapseBigDataPoolConfig",
        "id": "{% uuid 'v4' %}",
		"autoPauseProperties":{
            "enabled": true,
            "delayInMinutes": DESIRED_COUNT
        },
        "dynamicExecutorAllocation":{
            "enabled": true,
            "minExecutors": DESIRED_COUNT,
            "maxExecutors": DESIRED_COUNT
        },
        "sparkVersion": "spark3_3" // Possible values are: "spark3_1", "spark3_3"
	}
}

{
	"patch":
	{
		"@odata.type": "#microsoft.substrate.ai.management.bigDataPoolSize",
        "id": "{% uuid 'v4' %}",
		"minInstanceCount": DESIRED_COUNT,
        "maxInstanceCount": DESIRED_COUNT,
        "nodeSize": "medium" // Possible values are: "small", "medium", "large", "xlarge", "xxlarge", "xxxlarg"
	}
}

{
	"patch":
	{
		"@odata.type": "#microsoft.substrate.ai.management.assignSynapseRole", // Fixed
        "id": "{% uuid 'v4' %}", // Random Guid
		"synapseRoleName": "Synapse Administrator", // Possible values are available at https://docs.microsoft.com/en-us/azure/synapse-analytics/security/synapse-workspace-synapse-rbac-roles
        "objectId": "" // Guid of the AAD Object Id - Can be a User, Group, MSI, etc.
	}
}

